"use strict";

var mongoose = require("mongoose");
var Members = require("./memberSchema");
var Watches = require("./watchSchema");
// Định nghĩa commentSchema
var commentSchema = new mongoose.Schema({
  rating: {
    type: Number,
    min: 1,
    max: 3,
    required: true
  },
  content: {
    type: String,
    required: true
  },
  author: {
    type: mongoose.Schema.Types.ObjectId,
    ref: "Members",
    required: true
  },
  watch: {
    type: mongoose.Schema.Types.ObjectId,
    ref: "Watches",
    required: true
  }
});
module.exports = mongoose.model("Comment", commentSchema);
